import React, { useState } from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import './App.css';
import AuthPage from './components/AuthPage';
import NavBar from './components/NavBar';
import useAuthContext from './hooks/useAuthContext';
import ArticlePage from './components/ArticlePage';
import { Input } from 'semantic-ui-react';

function App() {
  const [uuid, setUuid] = useState("");
  return (
    <div className="App">
      <Routes>
        <Route path='/' element={<PublicPage />} />
        <Route path='/login' element={<AuthPage />} />
        <Route path='/logged' element={
          <RequireAuth>
            <LoggedIn />
          </RequireAuth>
        } />
        <Route path='/article/:uuid' element={
          <RequireAuth>
            <ArticlePage />
          </RequireAuth>
        } />
        <Route path="*" element={<NTSH />} />
      </Routes>
      <Routes />
    </div>
  );
}

function LoggedIn() {
  let { user } = useAuthContext()
  return (
    <>
      <NavBar />
      <Input value={uuid} onChange={({target}) => setUuid(target.value)} />
      <Link to={`/article/${uuid}`}>Tets</Link>
      <h1>LOGGED IN AS : {user}</h1>
    </>
  )
}

function PublicPage() {
  return (
    <div>
      <h1>Welcome to Ducking RSS</h1>
      <Link to="/logged">Login</Link>
    </div>
  )
}

function NTSH() {
  return (
    <div>
      <h1>You're lost there is nothing to see here</h1>
      <Link to="/">Go back Home</Link>
    </div>
  )
}

function RequireAuth({ children }) {
  let { user } = useAuthContext();
  if (!user) {
    return <Navigate replace to='/login' />;
  }
  return <>{children}</>
}

export default App;
