import React, { useState, useEffect } from 'react';
import { getLastArticles } from '../js/contentApi';
import ArticlePresentation from './ArticlePresentation';
import { Pagination } from 'semantic-ui-react';

function HomePage() {
    const pageSize = 5;
    const [activePage, setActivePage] = useState(1);
    const [numberOfPages, setNumberOfPages] = useState(1);
    const [lastArticles, setLastArticles] = useState([]);
    const [pages, setPages] = useState([[]]);

    function reduceArticlesInPages(articles) {
        const pages = [];
        let i = 0;

        while (i < articles.length) {
          pages.push(articles.slice(i, i + pageSize));
          i += pageSize;
        }

        return pages;
      }

    useEffect(() => {
        setLastArticles(getLastArticles());
        setNumberOfPages(Math.ceil(lastArticles.length/pageSize))
        setPages(reduceArticlesInPages(lastArticles))
        console.log(pages)
    }, [])
    return (
        <>
            {/* {pages[activePage - 1].map((article) => <ArticlePresentation article={article} />)} */}
            <Pagination defaultActivePage={1} totalPages={numberOfPages} onPageChange={({activePage}) => setActivePage(activePage)}/>
        </>
    )
}

export default HomePage;
