    { name: "Feed#10", id: "10" },
    { name: "Feed#11", id: "11" },
    { name: "Feed#12", id: "12" },
    { name: "Feed#13", id: "13" },
]


export const getFeeds = (username, token) => {
    return feedContent;
}

export const addFeed = (username, token, link) => {
    try {
        let domain = new URL(link)
        feedContent.push({ name: domain.hostname, id: domain.hostname })
        return true
    } catch (error) {
        return false
    }
}

