import React, { useEffect, useState } from 'react';
import { Menu, Header, Dropdown, Accordion, List, Input, Button, Icon } from 'semantic-ui-react';
import duck from '../ressources/dance-dancing-duck.gif';
import useAuthContext from '../hooks/useAuthContext';
import { disconnect } from '../js/auth';
import { addFeed, getFeeds } from '../js/contentApi';


function NavBar({children}) {

    const [activeItem, setActiveItem] = useState(null);
    const [feedContent, setFeedContent] = useState([]);
    const [newFeedLink, setNewFeedLink] = useState("");
    const [newFeedError, setNewFeedError] = useState(false);

    const handleItemClick = (e, { name }) => {
        if (activeItem !== name) {
            setActiveItem(name);
            return
        }
        setActiveItem("");
    };

    let { username, token, setUser, setToken } = useAuthContext();

    function generateFeedContent(username, token) {
        setFeedContent(getFeeds(username, token))
    }

    function handleNewFeed() {
        setNewFeedError(!addFeed(username, token, newFeedLink))
        generateFeedContent(username, token)
    }

    useEffect(() => {
        generateFeedContent(username, token)
    }, [])


    return (
        <div style={{ display: 'block' }}>
            <Menu vertical fixed='left' inverted>
                <Header as="h2" inverted>
                    <img src={duck} style={{ borderRadius: "100px", maxWidth: "200px", margin: "20px" }} alt="logo" />
                    <p>
                        Ducking RSS
                    </p>
                </Header>
                <Menu.Item>
                    <Header as="h3" inverted>
                        Home
                    </Header>
                    <Menu.Menu>
                        {/* <Menu.Item
                        name='communities'
                        onClick={handleItemClick}
                        active={activeItem === "communities"}
                        >
                        <Accordion inverted fluid>
                        <Accordion.Title
                        active={activeItem === "communities"}
                        index={0}
                        onClick={handleItemClick}>
                        Communities
                        </Accordion.Title>
                        <Accordion.Content>
                        test
                        </Accordion.Content>
                        </Accordion>
                    </Menu.Item> */}
                        <Menu.Item
                            name='feed'
                            >
                            <Accordion inverted>
                                <Accordion.Title
                                    name="feed"
                                    onClick={handleItemClick}
                                    >
                                    Feed
                                </Accordion.Title>
                                <Accordion.Content content={
                                    <List divided inverted >
                                        {feedContent.map((content) => (
                                            <List.Item as="a" key={content.id}>{content.name}</List.Item>
                                            ))}
                                    </List>
                                } active={activeItem === "feed"} />
                            </Accordion>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu.Item>
                <Menu.Item>
                    <Header as="h3" inverted>
                        Browse
                    </Header>
                    <Menu.Menu>
                        {/* <Menu.Item
                        name='discover'
                        onClick={handleItemClick}
                        active={activeItem === "discover"}
                    /> */}
                        <Menu.Item
                            name='add'
                            >
                            <Accordion inverted>
                                <Accordion.Title
                                    name="add"
                                    onClick={handleItemClick}
                                    >
                                    Add
                                </Accordion.Title>
                                <Accordion.Content
                                    active={activeItem === "add"}
                                    >
                                    <Input
                                        placeholder='http://my.rss.link.com'
                                        transparent
                                        inverted
                                        size='tiny'
                                        value={newFeedLink}
                                        onChange={({ target }) => setNewFeedLink(target.value)}
                                        action={{ icon: 'plus', onClick: handleNewFeed, size: 'mini', inverted: true }}
                                    />
                                    <span>{newFeedError}</span>
                                    {newFeedError && <span style={{ color: 'red' }}>You must enter a valid url</span>}
                                </Accordion.Content>
                            </Accordion>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu.Item>
                <Menu.Item>
                    <Header as="h3" inverted>
                        Settings
                    </Header>
                    <Menu.Menu inverted>
                        <Menu.Item inverted>
                            <Dropdown text='Languages' options={[{ key: 1, text: 'English', value: 1 }]} />
                        </Menu.Item>
                        <Menu.Item
                            name='disconnect'
                            onClick={() => disconnect(setUser, setToken)}
                            inverted>
                            <span style={{ color: 'red ' }}>Disconnect</span>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu.Item>
            </Menu>
            {children}
        </div>
    )
}

export default NavBar;
