import React from 'react';
import { Grid, Header, Image, Segment } from 'semantic-ui-react';
import data from '../rss_example.json'


function ArticlePresentation() {
    return (
        <Segment compact raised>
            <Grid centered relaxed='very'>
                <Grid.Row>
                    <Header size='small' as='a' href={data.link || ""}>
                        {data.title}
                    </Header>
                </Grid.Row>
                <Grid.Row>
                    <Image
                        src={data.image.url}
                        size='small'
                        as='a'
                        href={data.image.link || ""}
                        target='_blank' />
                </Grid.Row>
                <Grid.Row>
                    <Header size='small'>
                        {data.description}
                    </Header>
                </Grid.Row>
            </Grid>
        </Segment>
    )
}

export default ArticlePresentation;
